mapper.select:
SELECT e.error_name FROM downtime d JOIN error e ON d.error_id = e.error_id
WHERE d.machine_id = #{machineId}
GROUP BY e.error_name ORDER BY COUNT(*) DESC LIMIT 1

SELECT DATE_FORMAT(start_dt, '%Y-%m-%d') AS day_label, SUM(TIMESTAMPDIFF(MINUTE, start_dt, COALESCE(end_dt, NOW()))) AS total_minutes FROM downtime WHERE start_dt >= CURDATE() - INTERVAL 7 DAY
AND machine_id = #{machineId}
GROUP BY day_label ORDER BY day_label ASC;

SELECT d.downtime_id as id, d.start_dt as startTime, d.end_dt as endTime, TIMESTAMPDIFF(MINUTE, d.start_dt, COALESCE(d.end_dt, NOW())) as duration, d.error_id as errorCode, e.error_name as errorName, m.machine_name as machineName FROM downtime d JOIN error e ON d.error_id = e.error_id JOIN machine m ON d.machine_id = m.machine_id
WHERE d.machine_id = #{machineId}
ORDER BY d.start_dt DESC
